<?php
require_once  '../Modelo/Empleados.php';
include'../Modelo/Usuario.php';

$js=  file_get_contents('php://input');
$obj= json_decode($js);

$usuario= $obj->usuario;
$contrasena=$obj->contrasena;
$rol= $obj->rol;
