$cedula=$obj->cedula;

$myUser=new Usuario();

$myUser->insertarUsuario($usuario, $contrasena, $rol);

$usuario_id=$myUser->ultimoUsuario();
echo $usuario_id;


$myEmp= new Empleados();
$myEmp->insertarEmpleado($nombre, $cedula, $usuario_id);




