<?php
class Conexiones{
	
	public $DB_HOST = 'mysql:dbname=inside;host=localhost';
	public $DB_USER = 'root';
	public $DB_PASS = '';
	public $DB_NAME = 'inside';
	
	public static function getConexion(){
		
		try{
		$con = new PDO($this->DB_HOST,  $this->DB_USER, $this->DB_PASS);
		if($con){
			return $con;
		}else{
			return "Error";
		}
				
		}catch(PDOException $e){
			echo "ERROR".$e->getMessage();
			
		}
		
	}
	
	public function close(){
		$con=null;
		return $con;
	}
	
}

?>